import React from "react";
import styled from "styled-components";

function Footer() {
  //css section
  const Wrapper = styled.div`
    background-color: rgb(185, 133, 133);
    display: flex;
    align-items: center;
    justify-content: center;
    /* height: 5vh; */

    p {
      font-weight: bold;
    }
  `;
  return (
    <Wrapper>
      <div class="item2">
        <h4>Follow AllVoice</h4>
        <div class="footericon-container">
          <div class="icon">
            <i class="fa-brands fa-facebook"></i>
          </div>
          <div class="icon">
            <i class="fa-brands fa-twitter"></i>
          </div>
          <div class="icon">
            <i class="fa-brands fa-youtube"></i>
          </div>
          <div class="icon">
            <i class="fa-brands fa-instagram"></i>
          </div>
        </div>
      </div>
      <hr />
      <p class="text-center">
        <em>&copy;2022, AllVoice News-Managed by Idokoken</em>
      </p>
      <div>
        <div className="icon">
          <i className="fa fa-facebook"></i>
        </div>
      </div>
      <p className="text-light">Created by Idokoken, All Right Reserved</p>
    </Wrapper>
  );
}

export default Footer;
